package Arrays;


//4.To print No. of elements Present in an array.
public class NoOfElement {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int[] arr = new int[] {1,2,3,4,5};
		
		System.out.println("No. of element present in array : " + arr.length);
	}
}

//No. of element present in array : 5